'use strict';
var env = require("vs/base/common/platform");
var terminalService_1 = require("vs/workbench/parts/execution/electron-browser/terminalService");
var extensions_1 = require("vs/platform/instantiation/common/extensions");
var execution_1 = require("vs/workbench/parts/execution/common/execution");
if (env.isWindows) {
    extensions_1.registerSingleton(execution_1.ITerminalService, terminalService_1.WinTerminalService);
}
else if (env.isMacintosh) {
    extensions_1.registerSingleton(execution_1.ITerminalService, terminalService_1.MacTerminalService);
}
else if (env.isLinux) {
    extensions_1.registerSingleton(execution_1.ITerminalService, terminalService_1.LinuxTerminalService);
}
