var fs = require('fs');
var env = require('vs/base/common/platform');
var defaultTerminalLinux = 'xterm';
if (env.isLinux) {
    if (fs.existsSync('/etc/debian_version')) {
        defaultTerminalLinux = 'x-terminal-emulator';
    }
    else if (process.env.DESKTOP_SESSION === 'gnome' || process.env.DESKTOP_SESSION === 'gnome-classic') {
        defaultTerminalLinux = 'gnome-terminal';
    }
    else if (process.env.DESKTOP_SESSION === 'kde-plasma') {
        defaultTerminalLinux = 'konsole';
    }
    else if (process.env.COLORTERM) {
        defaultTerminalLinux = process.env.COLORTERM;
    }
    else if (process.env.TERM) {
        defaultTerminalLinux = process.env.TERM;
    }
}
exports.DEFAULT_TERMINAL_LINUX = defaultTerminalLinux;
exports.DEFAULT_TERMINAL_OSX = 'Terminal.app';
exports.DEFAULT_TERMINAL_WINDOWS = '%COMSPEC%';
