'use strict';
function findAll(regex, string) {
    if (!regex.global) {
        throw new Error('not global regex');
    }
    var result = [];
    var match;
    while (match = regex.exec(string)) {
        result.push(match[0]);
    }
    return result;
}
exports.findAll = findAll;
